import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {
  email: string = '';
  senha: string = '';
  erro: string = '';

  constructor(private authService: AuthService, private router: Router) {}

  entrar() {
    this.authService.login({ email: this.email, senha: this.senha }).subscribe(usuario => {
      if (usuario) {
        this.erro = '';

        // Redireciona dependendo da permissão
        if (usuario.permissao === 'corretor') {
          this.router.navigate(['/interna-corretor']); // página admin
        } else {
          this.router.navigate(['/home']); // página de usuário comum
        }

      } else {
        this.erro = 'Email ou senha inválidos';
      }
    }, erro => {
      this.erro = 'Erro ao tentar logar. Tente novamente.';
    });
  }
}
<app-header-publico></app-header-publico>
<div class="login-box">
  <div class="logo">
    <img src="assets/img/1.png" alt="Logo Prime Imobiliária">
  </div>

  <form (ngSubmit)="entrar()">
    <input
      type="text"
      placeholder="Usuário"
      [(ngModel)]="email"
      name="email"
      required
    >

    <input
      type="password"
      placeholder="Senha"
      [(ngModel)]="senha"
      name="senha"
      required
    >

    <button type="submit">Login</button>
  </form>

  <div class="links">
    <a href="#">Esqueceu sua senha?</a>
    <a href="#">Ajuda</a>
  </div>
</div>

<app-footer></app-footer>
<router-outlet></router-outlet>
