import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule } from '@angular/forms';

@Component({
  selector: 'app-cadastrar',
  templateUrl: './cadastrar.component.html',
  styleUrls: ['./cadastrar.component.scss'],
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule]
})
export class CadastrarComponent {
  imovelForm: FormGroup;

  private apiUrl = 'http://localhost:3004/imoveis'; // URL do db.json

  constructor(private fb: FormBuilder, private http: HttpClient) {
    this.imovelForm = this.fb.group({
      id: ['', Validators.required],
      titulo: ['', Validators.required],
      categoria: ['Casa', Validators.required],
      cidade: ['', Validators.required],
      descricao: [''],
      valor: ['', Validators.required],
    });
  }

  cadastrarImovel() {
    if (this.imovelForm.valid) {
      const novoImovel = this.imovelForm.value;

      this.http.post(this.apiUrl, novoImovel).subscribe({
        next: () => {
          alert('✅ Imóvel cadastrado com sucesso!');
          this.imovelForm.reset();
        },
        error: (err) => {
          console.error('Erro ao cadastrar imóvel:', err);
          alert('❌ Erro ao cadastrar imóvel');
        }
      });
    } else {
      alert('⚠️ Preencha todos os campos obrigatórios.');
    }
  }
}

<div class="cadastro-imovel">
  <h2>Cadastrar Imóvel</h2>

  <form [formGroup]="imovelForm" (ngSubmit)="cadastrarImovel()">
    <div class="linha">
      <div class="campo">
        <label for="id">ID</label>
        <input id="id" type="text" formControlName="id" />
      </div>

      <div class="campo flex-2">
        <label for="titulo">Título</label>
        <input id="titulo" type="text" formControlName="titulo" />
      </div>

      <div class="campo">
        <label for="categoria">Categoria</label>
        <select id="categoria" formControlName="categoria">
          <option>Casa</option>
          <option>Apartamento</option>
          <option>Terreno</option>
          <option>Comercial</option>
        </select>
      </div>
    </div>

    <div class="linha">
      <div class="campo flex-2">
        <label for="cidade">Cidade</label>
        <input id="cidade" type="text" formControlName="cidade" />
      </div>

      <div class="campo flex-2">
        <label for="valor">Valor</label>
        <input id="valor" type="number" formControlName="valor" />
      </div>
    </div>

    <div class="linha">
      <div class="campo flex-3">
        <label for="descricao">Descrição</label>
        <textarea id="descricao" rows="4" formControlName="descricao"></textarea>
      </div>
    </div>

    <button type="submit">Cadastrar Imóvel</button>
  </form>
</div>
